library(twitteR)library(tm)library(wordcloud)library(magrittr)get_tweets <- searchTwitter("",                            n=1000,                             lang="en",                             since = toString(Sys.Date()),                            geocode = '57.224,-4.493,250km') %>%  sapply(function(x) x$getText()) %>%  iconv(to = "utf-8")tweets <- (get_tweets[!is.na(get_tweets)])tweet_corpus <- Corpus(VectorSource(tweets)) %>%  tm_map(content_transformer(tolower)) %>%  tm_map(removeWords, stopwords("english")) %>%  tm_map(removePunctuation) %>%  tm_map(function(x) removeWords(x,                                c(#"scotland",                                 #"scottish",                                 #"scotlands",                                 #"england",                                 "fucking",                                 "amp",                                 "http",                                 "https")                               )       )png("wordcloud.png", width=640,height=640)wordcloud(tweet_corpus, max.words = 100, random.order = FALSE,          control = list(removePunctuation = TRUE),          colors=brewer.pal(8,"Dark2")          )dev.off()tweet("What were people in Scotland tweeting about today?",      mediaPath = "wordcloud.png")
